mapper.select:
SELECT ws.score_year,ws.score_all,ws.score_english,ws.score_politics,ws.score_math, ws.score_major,wm.major_name,wm.major_code,wm.major_type,wd.department_name, wsc.school_name,wsc.school_type,wsc.school_levels,wsc.school_website,wsc.school_recruit,wa.area_name,wa.area_type from (SELECT ws.score_year,ws.score_all,ws.score_english,ws.score_politics, ws.score_math,ws.score_major,ws.connect_id,si.sort_id from wx_score as ws INNER JOIN (SELECT sort_id,connect_id from (select (@i:=@i+1) as sort_id,avg(score_all) as score_all,avg(score_english) as score_english, AVG(score_politics) as score_politics,avg(score_math) as score_math,avg(score_major) as score_major,connect_id from wx_score,(SELECT @i:=0) as i where score_year in (SELECT score_year from wx_score GROUP BY score_year HAVING score_year>MAX(score_year)-3) GROUP BY connect_id) as ws
and #{scoreAll}>=ws.score_all and #{scoreMath}>=ws.score_math and #{scoreEnglish}>=ws.score_english and #{scorePolitics}>=ws.score_politics and #{scoreMajor}>=ws.score_major
) as si on ws.connect_id=si.connect_id) as ws INNER JOIN wx_mcd as wmc on ws.connect_id=wmc.connect_id INNER JOIN wx_major as wm on wm.major_id=wmc.major_id INNER JOIN wx_department as wd on wd.department_id=wmc.department_id INNER JOIN wx_school as wsc on wsc.school_id=wd.department_school_id INNER JOIN wx_area as wa on wsc.school_area=wa.area_id
and wm.major_name like concat('%', #{majorName}, '%') and wa.area_name like concat('%', #{areaName}, '%') and wm.major_type = #{majorType}
ORDER BY wsc.school_levels,ws.sort_id,score_year LIMIT 27

mapper.update:
update wx_score
score_year = #{scoreYear}, score_all = #{scoreAll}, score_math = #{scoreMath}, score_english = #{scoreEnglish}, score_politics = #{scorePolitics}, score_major = #{scoreMajor}, connect_id = #{scoreMajorId},
where score_id = #{scoreId}

mapper.select:
select tem.*,ws.school_id,ws.school_name from (
select * from wx_score where
score_year >= #{first_year}
and #{scoreAll}+#{floatScore} >= score_all
and score_all >= #{scoreAll}-#{floatScore}
and connect_id in(
select connect_id from wx_mcd where major_id in(
select major_id from wx_major where major_name=#{majorName}
)
)
) AS tem INNER JOIN wx_mcd as wmc on wmc.connect_id =tem.connect_id
INNER JOIN wx_department as wd on wd.department_id=wmc.department_id
INNER JOIN wx_school AS ws on ws.school_id=wd.department_school_id
ORDER BY ws.school_name

select school_name from wx_school where school_id in (
    select department_school_id from wx_department where department_id in(
        select department_id from wx_mcd where connect_id=#{connectId}
    )
)
